$token= $oauth2['access_token'];


// 第三步:根据全局access_token和openid查询用户信息
$openid = $oauth2['openid'];
$get_user_info_url = "https://api.weixin.qq.com/sns/userinfo?access_token=$token&openid=$openid&lang=zh_CN";
